'use strict';const crypto=require('crypto'),IV_LENGTH=16;class Scryptify{static encrypt(a,b){const c=crypto.randomBytes(IV_LENGTH),d=crypto.createCipheriv('aes-256-cbc',Buffer.from(b),c),e=d.update(a),f=Buffer.concat([e,d.final()]);return`${c.toString('hex')}:${f.toString('hex')}`}static decrypt(a,b){const c=a.split(':'),d=Buffer.from(c.shift(),'hex'),e=Buffer.from(c.join(':'),'hex'),f=crypto.createDecipheriv('aes-256-cbc',Buffer.from(b),d),g=f.update(e),h=Buffer.concat([g,f.final()]);return h.toString()}}module.exports=Scryptify;